package com.example.ArticleSearch.Controllers;import com.example.ArticleSearch.model.Article;import com.example.ArticleSearch.service.ArticleService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.*;@RestController@RequestMapping("/")public class ArticleController {    @Autowired    private ArticleService articleService;    @GetMapping    public void uploadData(){        articleService.addArticle();    }    @GetMapping("wiki/{title}")    public Article getTitle(@PathVariable String title){        return articleService.getArticle(title);    }}